mapper.select:
SELECT LOCKER_NO, LOCKER_PRICE
FROM LOCKER
WHERE LOCKER_NO = #{lockerNo}

mapper.insert:
    INSERT INTO LOCKER_RESERV (LOCKER_RESERV_NO
                             , LOCKER_START_DATE
                             , LOCKER_END_DATE
                             , LOCKER_TOTAL_PRICE
                             , TID
                             , MEMBER_NO
                             , LOCKER_NO)
	VALUES(SEQ_LOCKER_RESERV_NO.NEXTVAL
	               , #{lockerStartDate}
	               , #{lockerEndDate}
	               , #{lockerTotalPrice}
	               , #{tid}
	               , #{memberNo}
	               , #{lockerNo})

mapper.select:
SELECT COUNT(*)
FROM LOCKER_RESERV
WHERE MEMBER_NO = #{memberNo}
