mapper.select:
SELECT COUNT(*)
  FROM MEMBER
 WHERE MEMBER_ID = #{checkId}

mapper.insert:
INSERT INTO EMAIL_CERT(EMAIL
					 , CERT_NO
					 , CREATE_TIME)
			 VALUES(#{email}
			 	  , #{certNo}
			 	  , #{createTime})

mapper.select:
SELECT EMAIL, CERT_NO, CREATE_TIME
FROM EMAIL_CERT
WHERE EMAIL = #{email}
  AND CERT_NO = #{checkNo}
  AND CREATE_TIME >= SYSDATE - (3 / 1440)

SELECT MEMBER_ID
FROM MEMBER
WHERE MEMBER_NAME = #{memberName}
  AND EMAIL = #{email}
  AND KAKAO_LOGIN IS NULL

SELECT MEMBER_PWD
FROM MEMBER
WHERE MEMBER_ID = #{memberId}
  AND EMAIL = #{email}

mapper.update:
UPDATE MEMBER
   SET TEMP_PWD = #{tempPwd}
 WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER 
   SET MEMBER_NAME = #{memberName}
       , MEMBER_ID = #{memberId}
       , EMAIL = #{email}
       , BIRTH_DATE = #{birthDate}
       , PHONE = #{phone}
       , ADDRESS = #{address}
       , KAKAO_LOGIN = #{kakaoLogin}
    WHERE MEMBER_ID = #{memberId}
      AND MEMBER_STATUS = 'Y'

UPDATE MEMBER
   SET MEMBER_PWD = #{memberPwd}
     , TEMP_PWD = NULL
 WHERE MEMBER_NO = #{memberNo}
   AND MEMBER_STATUS = 'Y'

mapper.select:
SELECT MEMBER_PWD
  FROM MEMBER
 WHERE MEMBER_NO = #{memberNo}

SELECT TEMP_PWD
  FROM MEMBER
 WHERE MEMBER_NO = #{memberNo}
