  FROM MEMBER
 WHERE MEMBER_ID = #{checkId}

mapper.insert:
INSERT INTO EMAIL_CERT(EMAIL
					 , CERT_NO
					 , CREATE_TIME)
			 VALUES(#{email}
			 	  , #{certNo}
			 	  , #{createTime})

mapper.select:
SELECT EMAIL, CERT_NO, CREATE_TIME
FROM EMAIL_CERT
WHERE EMAIL = #{email}
  AND CERT_NO = #{checkNo}
  AND CREATE_TIME >= SYSDATE - (3 / 1440)

mapper.insert:
INSERT INTO EMAIL_CERT (
    EMAIL,
    CERT_NO,
    CREATE_TIME
) VALUES (
    #{email},
    #{certNo},
    #{createTime}
