mapper.select:
SELECT COUNT(*)
FROM PACKAGE_INFO
WHERE PACKAGE_SET = #{packageSet}

mapper.insert:
		INSERT INTO PACKAGE_RESERV(PACKAGE_RESERV_NO
								 , CHECK_IN_DATE
								 , CHECK_OUT_DATE
                                 , TID
                                 , MEMBER_NO
                                 , PACKAGE_NO)
		VALUES (SEQ_PACKAGE_RESERV_NO.NEXTVAL
						, #{checkInDate}
						, #{checkOutDate}
		                , #{tid}
		                , #{memberNo}
		                , #{packageNo})
