mapper.select:
SELECT LOST_NO, LOST_TITLE, LOST_LOCATION, LOST_CONTENT, COUNT, CREATE_DATE, LOST_ITEM_STATUS
FROM (
    SELECT ROWNUM AS RNUM, A.*
    FROM (
        SELECT *
        FROM LOST_ITEM
        WHERE LOST_STATUS = 'Y'
        ORDER BY CREATE_DATE DESC
    ) A
    WHERE ROWNUM <= #{endRow}
)
WHERE RNUM >= #{startRow}

SELECT LOST_NO, LOST_TITLE, LOST_LOCATION, LOST_CONTENT, COUNT, CREATE_DATE, LOST_ITEM_STATUS
FROM LOST_ITEM
WHERE LOST_NO = #{lostNo}

mapper.update:
UPDATE LOST_ITEM
SET COUNT = COUNT + 1
WHERE LOST_NO = #{lostNo}
