mapper.insert:
INSERT INTO PACKAGE_INFO(PACKAGE_NO
					   , PACKAGE_NAME
					   , PACKAGE_PRICE
					   , AVAILABLE_START_DATE
					   , AVAILABLE_END_DATE
					   , PRODUCT_COMPOSIT
					   , PACKAGE_SET
					   , PACK_ORIGIN_NAME
					   , PACK_CHANGE_NAME
					   , PACK_IMG_PATH)
VALUES(SEQ_PACKAGE_NO.NEXTVAL
	 , #{packageName}
	 , #{packagePrice}
	 , #{availableStartDate}
	 , #{availableEndDate}
	 , #{productComposit}
	 , #{packageSet}
	 , #{packOriginName}
	 , #{packChangeName}
	 , #{packImgPath})
