mapper.update:
UPDATE ROOM
SET CAPACITY = #{capacity}
  , ROOM_PRICE = #{roomPrice}
WHERE ROOM_NO = #{roomNo}

mapper.insert:
INSERT INTO ROOM(ROOM_NO
			   , ROOM_NAME
			   , ROOM_DESC
			   , ROOM_TYPE
			   , CAPACITY
			   , ROOM_PRICE)
VALUES(SEQ_ROOM_NO.NEXTVAL
	 , #{roomName}
	 , #{roomDesc}
	 , #{roomType}
	 , #{capacity}
	 , #{roomPrice})
